import{_ as e,c as a,o as r,a as t}from"./app.f3758f8e.js";const b=JSON.parse('{"title":"Open Source projects using Catch2","description":"","frontmatter":{},"headers":[{"level":2,"title":"Libraries & Frameworks","slug":"libraries-frameworks"},{"level":3,"title":"accessorpp","slug":"accessorpp"},{"level":3,"title":"alpaka","slug":"alpaka"},{"level":3,"title":"ApprovalTests.cpp","slug":"approvaltests-cpp"},{"level":3,"title":"args","slug":"args"},{"level":3,"title":"Azmq","slug":"azmq"},{"level":3,"title":"Cataclysm: Dark Days Ahead","slug":"cataclysm-dark-days-ahead"},{"level":3,"title":"ChaiScript","slug":"chaiscript"},{"level":3,"title":"ChakraCore","slug":"chakracore"},{"level":3,"title":"Clara","slug":"clara"},{"level":3,"title":"Couchbase-lite-core","slug":"couchbase-lite-core"},{"level":3,"title":"cppcodec","slug":"cppcodec"},{"level":3,"title":"DtCraft","slug":"dtcraft"},{"level":3,"title":"eventpp","slug":"eventpp"},{"level":3,"title":"forest","slug":"forest"},{"level":3,"title":"Fuxedo","slug":"fuxedo"},{"level":3,"title":"HIP CPU Runtime","slug":"hip-cpu-runtime"},{"level":3,"title":"Inja","slug":"inja"},{"level":3,"title":"LLAMA","slug":"llama"},{"level":3,"title":"libcluon","slug":"libcluon"},{"level":3,"title":"MNMLSTC Core","slug":"mnmlstc-core"},{"level":3,"title":"nanodbc","slug":"nanodbc"},{"level":3,"title":"Nonius","slug":"nonius"},{"level":3,"title":"OpenALpp","slug":"openalpp"},{"level":3,"title":"polymorphic_value","slug":"polymorphic-value"},{"level":3,"title":"Ppconsul","slug":"ppconsul"},{"level":3,"title":"Reactive-Extensions/ RxCpp","slug":"reactive-extensions-rxcpp"},{"level":3,"title":"SOCI","slug":"soci"},{"level":3,"title":"TextFlowCpp","slug":"textflowcpp"},{"level":3,"title":"thor","slug":"thor"},{"level":3,"title":"toml++","slug":"toml"},{"level":3,"title":"Trompeloeil","slug":"trompeloeil"},{"level":2,"title":"Applications & Tools","slug":"applications-tools"},{"level":3,"title":"App Mesh","slug":"app-mesh"},{"level":3,"title":"ArangoDB","slug":"arangodb"},{"level":3,"title":"Cytopia","slug":"cytopia"},{"level":3,"title":"d-SEAMS","slug":"d-seams"},{"level":3,"title":"Giada - Your Hardcore Loop Machine","slug":"giada-your-hardcore-loop-machine"},{"level":3,"title":"MAME","slug":"mame"},{"level":3,"title":"Newsbeuter","slug":"newsbeuter"},{"level":3,"title":"PopHead","slug":"pophead"},{"level":3,"title":"raspigcd","slug":"raspigcd"},{"level":3,"title":"SpECTRE","slug":"spectre"},{"level":3,"title":"Standardese","slug":"standardese"}],"relativePath":"opensource-users.md","lastUpdated":1654607170000}'),o={name:"opensource-users.md"},n=t('<p><a id="top"></a></p><h1 id="open-source-projects-using-catch2" tabindex="-1">Open Source projects using Catch2 <a class="header-anchor" href="#open-source-projects-using-catch2" aria-hidden="true">#</a></h1><p>Catch2 is great for open source. It is licensed under the <a href="../LICENSE.txt">Boost Software License (BSL)</a>, has no further dependencies and supports two file distribution.</p><p>As a result, Catch2 is used for testing in many different Open Source projects. This page lists at least some of them, even though it will obviously never be complete (and does not have the ambition to be complete). Note that the list below is intended to be in alphabetical order, to avoid implications of relative importance of the projects.</p><p><em>Please only add projects here if you are their maintainer, or have the maintainer&#39;s explicit consent.</em></p><h2 id="libraries-frameworks" tabindex="-1">Libraries &amp; Frameworks <a class="header-anchor" href="#libraries-frameworks" aria-hidden="true">#</a></h2><h3 id="accessorpp" tabindex="-1"><a href="https://github.com/wqking/accessorpp" target="_blank" rel="noopener noreferrer">accessorpp</a> <a class="header-anchor" href="#accessorpp" aria-hidden="true">#</a></h3><p>C++ library for implementing property and data binding.</p><h3 id="alpaka" tabindex="-1"><a href="https://github.com/alpaka-group/alpaka" target="_blank" rel="noopener noreferrer">alpaka</a> <a class="header-anchor" href="#alpaka" aria-hidden="true">#</a></h3><p>A header-only C++14 abstraction library for accelerator development.</p><h3 id="approvaltests-cpp" tabindex="-1"><a href="https://github.com/approvals/ApprovalTests.cpp" target="_blank" rel="noopener noreferrer">ApprovalTests.cpp</a> <a class="header-anchor" href="#approvaltests-cpp" aria-hidden="true">#</a></h3><p>C++11 implementation of Approval Tests, for quick, convenient testing of legacy code.</p><h3 id="args" tabindex="-1"><a href="https://github.com/Taywee/args" target="_blank" rel="noopener noreferrer">args</a> <a class="header-anchor" href="#args" aria-hidden="true">#</a></h3><p>A simple header-only C++ argument parser library.</p><h3 id="azmq" tabindex="-1"><a href="https://github.com/zeromq/azmq" target="_blank" rel="noopener noreferrer">Azmq</a> <a class="header-anchor" href="#azmq" aria-hidden="true">#</a></h3><p>Boost Asio style bindings for ZeroMQ.</p><h3 id="cataclysm-dark-days-ahead" tabindex="-1"><a href="https://github.com/CleverRaven/Cataclysm-DDA" target="_blank" rel="noopener noreferrer">Cataclysm: Dark Days Ahead</a> <a class="header-anchor" href="#cataclysm-dark-days-ahead" aria-hidden="true">#</a></h3><p>Post-apocalyptic survival RPG.</p><h3 id="chaiscript" tabindex="-1"><a href="https://github.com/ChaiScript/ChaiScript" target="_blank" rel="noopener noreferrer">ChaiScript</a> <a class="header-anchor" href="#chaiscript" aria-hidden="true">#</a></h3><p>A, header-only, embedded scripting language designed from the ground up to directly target C++ and take advantage of modern C++ development techniques.</p><h3 id="chakracore" tabindex="-1"><a href="https://github.com/Microsoft/ChakraCore" target="_blank" rel="noopener noreferrer">ChakraCore</a> <a class="header-anchor" href="#chakracore" aria-hidden="true">#</a></h3><p>The core part of the Chakra JavaScript engine that powers Microsoft Edge.</p><h3 id="clara" tabindex="-1"><a href="https://github.com/philsquared/Clara" target="_blank" rel="noopener noreferrer">Clara</a> <a class="header-anchor" href="#clara" aria-hidden="true">#</a></h3><p>A, single-header-only, type-safe, command line parser - which also prints formatted usage strings.</p><h3 id="couchbase-lite-core" tabindex="-1"><a href="https://github.com/couchbase/couchbase-lite-core" target="_blank" rel="noopener noreferrer">Couchbase-lite-core</a> <a class="header-anchor" href="#couchbase-lite-core" aria-hidden="true">#</a></h3><p>The next-generation core storage and query engine for Couchbase Lite.</p><h3 id="cppcodec" tabindex="-1"><a href="https://github.com/tplgy/cppcodec" target="_blank" rel="noopener noreferrer">cppcodec</a> <a class="header-anchor" href="#cppcodec" aria-hidden="true">#</a></h3><p>Header-only C++11 library to encode/decode base64, base64url, base32, base32hex and hex (a.k.a. base16) as specified in RFC 4648, plus Crockford&#39;s base32.</p><h3 id="dtcraft" tabindex="-1"><a href="https://github.com/twhuang-uiuc/DtCraft" target="_blank" rel="noopener noreferrer">DtCraft</a> <a class="header-anchor" href="#dtcraft" aria-hidden="true">#</a></h3><p>A High-performance Cluster Computing Engine.</p><h3 id="eventpp" tabindex="-1"><a href="https://github.com/wqking/eventpp" target="_blank" rel="noopener noreferrer">eventpp</a> <a class="header-anchor" href="#eventpp" aria-hidden="true">#</a></h3><p>C++ event library for callbacks, event dispatcher, and event queue. With eventpp you can easily implement signal and slot mechanism, publisher and subscriber pattern, or observer pattern.</p><h3 id="forest" tabindex="-1"><a href="https://github.com/xorz57/forest" target="_blank" rel="noopener noreferrer">forest</a> <a class="header-anchor" href="#forest" aria-hidden="true">#</a></h3><p>Template Library of Tree Data Structures.</p><h3 id="fuxedo" tabindex="-1"><a href="https://github.com/fuxedo/fuxedo" target="_blank" rel="noopener noreferrer">Fuxedo</a> <a class="header-anchor" href="#fuxedo" aria-hidden="true">#</a></h3><p>Open source Oracle Tuxedo-like XATMI middleware for C and C++.</p><h3 id="hip-cpu-runtime" tabindex="-1"><a href="https://github.com/ROCm-Developer-Tools/HIP-CPU" target="_blank" rel="noopener noreferrer">HIP CPU Runtime</a> <a class="header-anchor" href="#hip-cpu-runtime" aria-hidden="true">#</a></h3><p>A header-only library that allows CPUs to execute unmodified HIP code. It is generic and does not assume a particular CPU vendor or architecture.</p><h3 id="inja" tabindex="-1"><a href="https://github.com/pantor/inja" target="_blank" rel="noopener noreferrer">Inja</a> <a class="header-anchor" href="#inja" aria-hidden="true">#</a></h3><p>A header-only template engine for modern C++.</p><h3 id="llama" tabindex="-1"><a href="https://github.com/alpaka-group/llama" target="_blank" rel="noopener noreferrer">LLAMA</a> <a class="header-anchor" href="#llama" aria-hidden="true">#</a></h3><p>A C++17 template header-only library for the abstraction of memory access patterns.</p><h3 id="libcluon" tabindex="-1"><a href="https://github.com/chrberger/libcluon" target="_blank" rel="noopener noreferrer">libcluon</a> <a class="header-anchor" href="#libcluon" aria-hidden="true">#</a></h3><p>A single-header-only library written in C++14 to glue distributed software components (UDP, TCP, shared memory) supporting natively Protobuf, LCM/ZCM, MsgPack, and JSON for dynamic message transformations in-between.</p><h3 id="mnmlstc-core" tabindex="-1"><a href="https://github.com/mnmlstc/core" target="_blank" rel="noopener noreferrer">MNMLSTC Core</a> <a class="header-anchor" href="#mnmlstc-core" aria-hidden="true">#</a></h3><p>A small and easy to use C++11 library that adds a functionality set that will be available in C++14 and later, as well as some useful additions.</p><h3 id="nanodbc" tabindex="-1"><a href="https://github.com/lexicalunit/nanodbc/" target="_blank" rel="noopener noreferrer">nanodbc</a> <a class="header-anchor" href="#nanodbc" aria-hidden="true">#</a></h3><p>A small C++ library wrapper for the native C ODBC API.</p><h3 id="nonius" tabindex="-1"><a href="https://github.com/libnonius/nonius" target="_blank" rel="noopener noreferrer">Nonius</a> <a class="header-anchor" href="#nonius" aria-hidden="true">#</a></h3><p>A header-only framework for benchmarking small snippets of C++ code.</p><h3 id="openalpp" tabindex="-1"><a href="https://github.com/Laguna1989/OpenALpp" target="_blank" rel="noopener noreferrer">OpenALpp</a> <a class="header-anchor" href="#openalpp" aria-hidden="true">#</a></h3><p>A modern OOP C++14 audio library built on OpenAL for Windows, Linux and web (emscripten).</p><h3 id="polymorphic-value" tabindex="-1"><a href="https://github.com/jbcoe/polymorphic_value" target="_blank" rel="noopener noreferrer">polymorphic_value</a> <a class="header-anchor" href="#polymorphic-value" aria-hidden="true">#</a></h3><p>A polymorphic value-type for C++.</p><h3 id="ppconsul" tabindex="-1"><a href="https://github.com/oliora/ppconsul" target="_blank" rel="noopener noreferrer">Ppconsul</a> <a class="header-anchor" href="#ppconsul" aria-hidden="true">#</a></h3><p>A C++ client library for Consul. Consul is a distributed tool for discovering and configuring services in your infrastructure.</p><h3 id="reactive-extensions-rxcpp" tabindex="-1"><a href="https://github.com/Reactive-Extensions/RxCpp" target="_blank" rel="noopener noreferrer">Reactive-Extensions/ RxCpp</a> <a class="header-anchor" href="#reactive-extensions-rxcpp" aria-hidden="true">#</a></h3><p>A library of algorithms for values-distributed-in-time.</p><h3 id="soci" tabindex="-1"><a href="https://github.com/SOCI/soci" target="_blank" rel="noopener noreferrer">SOCI</a> <a class="header-anchor" href="#soci" aria-hidden="true">#</a></h3><p>The C++ Database Access Library.</p><h3 id="textflowcpp" tabindex="-1"><a href="https://github.com/philsquared/textflowcpp" target="_blank" rel="noopener noreferrer">TextFlowCpp</a> <a class="header-anchor" href="#textflowcpp" aria-hidden="true">#</a></h3><p>A small, single-header-only, library for wrapping and composing columns of text.</p><h3 id="thor" tabindex="-1"><a href="https://github.com/xorz57/thor" target="_blank" rel="noopener noreferrer">thor</a> <a class="header-anchor" href="#thor" aria-hidden="true">#</a></h3><p>Wrapper Library for CUDA.</p><h3 id="toml" tabindex="-1"><a href="https://github.com/marzer/tomlplusplus" target="_blank" rel="noopener noreferrer">toml++</a> <a class="header-anchor" href="#toml" aria-hidden="true">#</a></h3><p>A header-only TOML parser and serializer for modern C++.</p><h3 id="trompeloeil" tabindex="-1"><a href="https://github.com/rollbear/trompeloeil" target="_blank" rel="noopener noreferrer">Trompeloeil</a> <a class="header-anchor" href="#trompeloeil" aria-hidden="true">#</a></h3><p>A thread-safe header-only mocking framework for C++14.</p><h2 id="applications-tools" tabindex="-1">Applications &amp; Tools <a class="header-anchor" href="#applications-tools" aria-hidden="true">#</a></h2><h3 id="app-mesh" tabindex="-1"><a href="https://github.com/laoshanxi/app-mesh" target="_blank" rel="noopener noreferrer">App Mesh</a> <a class="header-anchor" href="#app-mesh" aria-hidden="true">#</a></h3><p>A high available cloud native micro-service application management platform implemented by modern C++.</p><h3 id="arangodb" tabindex="-1"><a href="https://github.com/arangodb/arangodb" target="_blank" rel="noopener noreferrer">ArangoDB</a> <a class="header-anchor" href="#arangodb" aria-hidden="true">#</a></h3><p>ArangoDB is a native multi-model database with flexible data models for documents, graphs, and key-values.</p><h3 id="cytopia" tabindex="-1"><a href="https://github.com/CytopiaTeam/Cytopia" target="_blank" rel="noopener noreferrer">Cytopia</a> <a class="header-anchor" href="#cytopia" aria-hidden="true">#</a></h3><p>Cytopia is a free, open source retro pixel-art city building game with a big focus on mods. It utilizes a custom isometric rendering engine based on SDL2.</p><h3 id="d-seams" tabindex="-1"><a href="https://github.com/d-SEAMS/seams-core" target="_blank" rel="noopener noreferrer">d-SEAMS</a> <a class="header-anchor" href="#d-seams" aria-hidden="true">#</a></h3><p>Open source molecular dynamics simulation structure analysis suite of tools in modern C++.</p><h3 id="giada-your-hardcore-loop-machine" tabindex="-1"><a href="https://github.com/monocasual/giada" target="_blank" rel="noopener noreferrer">Giada - Your Hardcore Loop Machine</a> <a class="header-anchor" href="#giada-your-hardcore-loop-machine" aria-hidden="true">#</a></h3><p>Minimal, open-source and cross-platform audio tool for live music production.</p><h3 id="mame" tabindex="-1"><a href="https://github.com/mamedev/mame" target="_blank" rel="noopener noreferrer">MAME</a> <a class="header-anchor" href="#mame" aria-hidden="true">#</a></h3><p>MAME originally stood for Multiple Arcade Machine Emulator.</p><h3 id="newsbeuter" tabindex="-1"><a href="https://github.com/akrennmair/newsbeuter" target="_blank" rel="noopener noreferrer">Newsbeuter</a> <a class="header-anchor" href="#newsbeuter" aria-hidden="true">#</a></h3><p>Newsbeuter is an open-source RSS/Atom feed reader for text terminals.</p><h3 id="pophead" tabindex="-1"><a href="https://github.com/SPC-Some-Polish-Coders/PopHead" target="_blank" rel="noopener noreferrer">PopHead</a> <a class="header-anchor" href="#pophead" aria-hidden="true">#</a></h3><p>A 2D, Zombie, RPG game which is being made on our own engine.</p><h3 id="raspigcd" tabindex="-1"><a href="https://github.com/pantadeusz/raspigcd" target="_blank" rel="noopener noreferrer">raspigcd</a> <a class="header-anchor" href="#raspigcd" aria-hidden="true">#</a></h3><p>Low level CLI app and library for execution of GCODE on Raspberry Pi without any additional microcontrolers (just RPi + Stepsticks).</p><h3 id="spectre" tabindex="-1"><a href="https://github.com/sxs-collaboration/spectre" target="_blank" rel="noopener noreferrer">SpECTRE</a> <a class="header-anchor" href="#spectre" aria-hidden="true">#</a></h3><p>SpECTRE is a code for multi-scale, multi-physics problems in astrophysics and gravitational physics.</p><h3 id="standardese" tabindex="-1"><a href="https://github.com/foonathan/standardese" target="_blank" rel="noopener noreferrer">Standardese</a> <a class="header-anchor" href="#standardese" aria-hidden="true">#</a></h3><p>Standardese aims to be a nextgen Doxygen.</p><hr><p><a href="./Readme.html#top">Home</a></p>',93),i=[n];function l(s,h,p,d,c,u){return r(),a("div",null,i)}var m=e(o,[["render",l]]);export{b as __pageData,m as default};
